INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1822, 1, '당신을 해치려고 온 게 아닙니다, 카란. 고르샤크가 보냈습니다. 당신이 공주에 대해 말해 줄 거라더군요.', 'Je ne suis pas ici pour vous faire du mal, Kharan. Gor\'shak m\'envoie. Il m\'a dit que vous me parleriez de la princesse.', 'Ich bin nicht hier, um Euch Leid zuzufügen, Kharan. Gor\'shak schickt mich. Er sagte, Ihr würdet mit mir über die Prinzessin sprechen.', '我不是来伤害你的，卡兰。哥沙克派我来找你。他说你会告诉我关于公主的事情。', '我不是來傷害你的，卡蘭。哥沙克派我來找你。他說你會告訴我關於公主的事情。', 'No he venido a hacerte daño, Kharan. Gor\'shak me ha enviado. Me dijo que me hablarías sobre la princesa.', 'No he venido a hacerte daño, Kharan. Gor\'shak me ha enviado. Me dijo que me hablarías sobre la princesa.', 'Я не причиню тебе вреда, Каран. Меня послал Гор\'шак. Он сказал, что ты расскажешь мне о принцессе.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1823, 0, '문이 열렸습니다, 카란. 이제 당신은 자유입니다.', 'La porte est ouverte, Kharan. Vous êtes un homme libre.', 'Die Tür ist offen, Kharan. Ihr seid ein freier Mann.', '卡兰，门开了。你自由了。', '卡蘭，門開了。你自由了。', 'La puerta está abierta, Kharan. Eres un hombre libre.', 'La puerta está abierta, Kharan. Eres un hombre libre.', 'Дверь открыта, Каран. Вы свободны.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
