INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1832, 0, '당신은 아직 살아 있고 내가 당신을 죽이려고 하지 않기 때문이죠.', 'Parce que vous êtes toujours en vie et que mes mains ne sont pas en train de serrer votre épais cou de goret.', 'Weil Ihr immer noch am Leben seid und meine Hände nicht fest um Euren feisten Hals geklammert sind.', '因为你还活着，我的手也没有卡在你结实的短脖子上。', '因為你還活著，我的手也沒有卡在你結實的短脖子上。', 'Porque tú sigues vivo y mis manos no aprietan tu pequeño y grueso cuello.', 'Porque tú sigues vivo y mis manos no aprietan tu pequeño y grueso cuello.', 'Потому что ты все еще дышишь и мои руки не сжимают твою глотку.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1833, 0, '전혀 모릅니다. 내가 받은 명령은 당신과 얘기를 나누고 스랄 님을 만나러 가라는 것이었습니다. 아는 거라곤 스랄 님이 당신네 공주를 구하고 싶어한다는 것뿐입니다.', 'Rien. Mes ordres étaient de parler avec vous puis avec Thrall. Tout ce que je sais, c\'est que Thrall veut sauver votre princesse.', 'Nichts. Meine Befehle lauteten, erst mit Euch und dann mit Thrall zu sprechen. Ich weiß nur, dass Thrall Interesse daran hat, Eure Prinzessin zu retten.', '没什么。我的任务是跟你谈谈，然后回去找萨尔。萨尔对营救公主的事情很有兴趣。', '沒什麼。我的任務是跟你談談，然後回去找索爾。索爾對營救公主的事情很有興趣。', 'Nada. Mis órdenes eran hablar contigo y luego hablar con Thrall. Todo lo que sé es que Thrall está interesado en salvar a tu princesa.', 'Nada. Mis órdenes eran hablar contigo y luego hablar con Thrall. Todo lo que sé es que Thrall está interesado en salvar a tu princesa.', 'Ничего. Мне было приказано поговорить сначала с тобой, а потом с Траллом. Я только знаю, что Тралл заинтересован в том, чтобы спасти вашу принцессу.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1834, 0, '그런데도 산 밑바닥의 감옥에 들어앉아 있게 되었단 말입니까?', 'Ce qui explique pourquoi vous êtes assis dans une cellule en bas de la montagne, n\'est-ce pas, nain ?', 'Was erklärt, weshalb Ihr in einer Gefängniszelle unter einem Berg sitzt, Zwerg. Richtig?', '这就解释了你被关在黑石山下的囚房里的原因，是吧，矮人？', '這就解釋了你被關在黑石山下的囚房裡的原因，是吧，矮人?', 'Lo que explicaría por qué estás recluido en la celda de una prisión en el interior de una montaña, ¿verdad, enano?', 'Lo que explicaría por qué estás recluido en la celda de una prisión en el interior de una montaña, ¿verdad, enano?', 'Именно поэтому ты и сидишь в тюремной камере под горой, да, дворф?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
