INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1839, 0, '위로가 될지는 모르겠지만 감옥 문을 열어 드리도록 하겠습니다. 여기서 빠져 나가는 건 당신이 알아서 하십시오. 잘 가세요, 카란.', 'Si cela peut vous consoler, je vais laisser la porte de la cellule ouverte. Comment vous sortirez d\'ici, ça vous regarde. Adieu, Kharan.', 'Ich werde die Zellentür offen lassen, falls das ein Trost ist. Wie Ihr hinausgelangt, ist Euer Problem. Lebt wohl, Kharan.', '我会把门开着的，你怎么逃出去是你的问题。再见，卡兰。', '我會把門開著的，你怎麼逃出去是你的問題。再見，卡蘭。', 'Si te sirve de consuelo, dejaré abierta la puerta de la celda. Como salgas es tu problema. Adiós, Kharan.', 'Si te sirve de consuelo, dejaré abierta la puerta de la celda. Como salgas es tu problema. Adiós, Kharan.', 'Если это поможет, я оставлю дверь камеры открытой. Как ты отсюда выберешься – твое дело. Прощай, Каран.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1842, 0, '증거물을 가져왔습니다, 키루스.', 'Je vous présente les preuves de mes actes, Cyrus.', 'Ich präsentiere Euch Beweise meiner Taten, Cyrus.', '我让你看看我的能力的证明，塞勒斯。', '我讓你看看我的能力的證明，塞勒斯。', 'Vengo a presentaros pruebas de mis hechos, Cyrus.', 'Vengo a presentaros pruebas de mis hechos, Cyrus.', 'Вот доказательства моих деяний, Цирус.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (1842, 1, '아직 증거물을 가져오지 않았습니다, 키루스.', 'Je n\'ai aucune preuve, Cyrus.', 'Ich habe keine Beweise, Cyrus.', '塞勒斯，我没有任何证据。', '塞勒斯，我沒有任何證據。', 'No poseo prueba alguna, Cyrus.', 'No poseo prueba alguna, Cyrus.', 'У меня нет никаких доказательств, Цирус.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
