INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (2995, 0, '그러니까 당신 말은 이... 용암 방열복이라는 걸 입고 용암 속에서 헤엄을... 아니, 배영을 했다는 거죠? 그리고 갑자기 이 괴물이 나타나서는 당신을 통째로 집어삼켰고요?', 'Alors parlons franc : vous étiez en train de nager en dos crawlé dans la lave avec ce... cette combinaison de votre invention ? C\'est ça ? Et tout à coup, venant de nulle part, cette bête vous a avalé ?', 'Nur, damit ich das richtig verstehe: Ihr seid also mit diesem... diesem Lavaanzug da in geschmolzener Lava herumgeschwommen, auf dem Rücken? Ja? Und dann hat Euch einfach so, aus heiterem Himmel, diese Bestie hier in einem Stück verschluckt?', '让我搞搞清楚。你穿着这件……这件熔岩服，在火红的岩浆里面游泳，不，仰泳？而且在毫无察觉的情况下，被这只野兽吞下了肚？', '讓我搞清楚。你穿著這件...這件熔岩服，在火紅的熔岩裡面游泳，不，仰泳?而且在毫無察覺的情況下，被這隻野獸吞下了肚?', '¡Puedes quedarte con ese, ya no lo necesito!', '¡Puedes quedarte con ese, ya no lo necesito!', 'Давай поговорим начистоту. Все случилось во время купания – нет, плескания в расплавленной лаве в этом... в этом лавовом костюме? Так? И вдруг как из ниоткуда появился этот зверь и целиком тебя проглотил?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (2996, 0, '한가지 알고 싶은 게 있는데, 그걸 입고 화장실은 어떻게 갑니까?', 'Je dois savoir quelque chose : comment vous êtes-vous soulagé, avec ce truc sur le dos ?', 'Eine Sache muss ich einfach wissen. Wie seid Ihr in diesem Anzug auf die Toilette gegangen?', '我必须知道一些事情：你如何穿着这个东西解决个人问题的？', '我必須知道一些事情:你如何穿著這個東西解決個人問題的?', 'Necesito saber algo: ¿cómo hacías tus necesidades con esa cosa puesta?', 'Necesito saber algo: ¿cómo hacías tus necesidades con esa cosa puesta?', 'Мне вот интересно: как ты с этой штукой принимаешь ванну?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (2997, 0, '사례라고요?', 'Oh ?', 'Ach?', '哦？', '哦?', '¿Oh?', '¿Oh?', 'Хм?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (2998, 0, '다음번에 이런 괴물을 보면 잘 기억해 두죠.', 'J\'y penserai la prochaine fois que je croiserai un de ces trucs.', 'Wenn ich das nächste Mal auf eines von diesen Viechern treffe, dann werde ich daran denken.', '下次再遇到这种事情的时候我会好好考虑一下的。', '下次再遇到這種事情的時候我會好好考慮一下的。', 'Lo tendré en cuenta la próxima vez que me encuentre con una de esas cosas.', 'Lo tendré en cuenta la próxima vez que me encuentre con una de esas cosas.', 'Думаю, что в следующий раз я наткнусь на них.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (2999, 0, '물론, 말리퍼스에게 전할 소식이겠죠.', 'À Malyfous, je suppose.', 'Für Malyfous, nehme ich an.', '我想，你应该去玛雷弗斯那里。', '我想，你應該去瑪雷弗斯那裡。', 'A Malyfous, supongo', 'A Malyfous, supongo', 'Полагаю, к Малифою.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3044, 0, '제가 도와드릴 수 있을지도 모르겠군요. 사티로스 로락스, 당신과 거래를 하겠습니다. 고랄루크 앤빌크랙이라고 하는 녀석을 처치하고 버려지지 않은 가슴보호갑을 찾아오면 그 대가로 악마의 룬 가슴보호갑 만드는 법을 가르쳐주십시오.', 'Je peux peut-être vous aider. Je vais faire un marché avec vous, satyre. Je vais retrouver ce plastron et tuer cette bête de Goraluk Brisenclume. En échange, apprenez-moi à créer le plastron.', 'Vielleicht kann ich Euch ja behilflich sein. Ich schlage Euch einen Handel vor, Satyr: Ich beschaffe Euch diese ungeschmiedete Brustplatte und töte die Bestie, Goraluk Hammerbruch. Und im Gegenzug lehrt Ihr mich, wie man die Brustplatte herstellt.', '或许我能帮上忙。萨特，我们来谈笔交易吧。我去取回这块未铸造的胸甲并杀死野兽古拉鲁克。作为交换，你要教我如何制造胸甲。', '或許我能幫上忙。薩特，我們來談筆交易吧。我去取回這塊未鑄造的胸甲並殺死野獸古拉魯克。作為交換，你要教我如何製造胸甲。', 'Quizás pueda ser de ayuda. Haré un trato contigo, sátiro. Recuperaré esa coraza sin forjar y derrotaré a la bestia, Goraluk Yunquegrieta. A cambio de esta tarea me enseñarás a crear el peto.', 'Quizás pueda ser de ayuda. Haré un trato contigo, sátiro. Recuperaré esa coraza sin forjar y derrotaré a la bestia, Goraluk Yunquegrieta. A cambio de esta tarea me enseñarás a crear el peto.', 'Возможно, я смогу тебе помочь. Я заключу с тобой сделку, сатир. Я добуду этот распаянный нагрудник и убью Горалука Треснувшую Наковальню. А ты покажешь мне, как делать нагрудники.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3045, 0, '고랄루크가 어떻게 계약을 깨뜨렸습니까?', 'Comment a-t-il rompu le marché ?', 'Wie hat er denn die Vereinbarung gebrochen?', '他是怎么破坏协议的？', '他是怎麼破壞協議的?', 'Entonces, ¿cómo rompió el trato?', 'Entonces, ¿cómo rompió el trato?', 'А как именно он нарушил сделку?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3046, 0, '어떤 계약이었죠?', 'Quel marché ?', 'Was für eine Vereinbarung?', '什么交易？', '什麼交易?', '¿Qué trato?', '¿Qué trato?', 'В чем дело?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
