INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3667, 0, '대족장님, 주제넘은 말인 줄 압니다만, 얼라이언스 녀석들이 무너지도록 내버려두면 좋지 않습니까? 그러면 우리 호드의 지위가 더욱 굳건해지지 않을까요?', 'Avec le respect que je vous dois, redouté chef de guerre, pourquoi ne pas les laisser se faire détruire ? Cela ne renforcerait-il pas notre position ?', 'Mit allem gebotenen Respekt, Kriegshäuptling - warum lassen wir nicht zu, dass es zerstört wird? Stärkt das nicht unsere Position?', '恕我直言，统帅大人--为什么不消灭他们？难道这不会巩固我们的地位吗？', '恕我直言，大酋長--為什麼不消滅他們?難道這不會鞏固我們的地位嗎?', 'Con todo el respeto, Jefe de Guerra... ¿Por qué no dejar que sean destruidos? ¿No reforzaría eso nuestra posición?', 'Con todo el respeto, Jefe de Guerra... ¿Por qué no dejar que sean destruidos? ¿No reforzaría eso nuestra posición?', 'Со всем уважением, вождь – но почему бы нам не допустить их уничтожения? Разве это не укрепит наши позиции?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3668, 0, '그... 그런 생각은 미처 하지 못했습니다, 대족장님.', 'Je... Je n\'y pensais pas en ces termes, Chef.', 'Ich... So habe ich das nicht gesehen, Kriegshäuptling.', '我……我可不这样想，战争之王。', '我...我可不這樣想，大酋長。', 'Ehh... No lo había pensado así, Jefe de Guerra.', 'Ehh... No lo había pensado así, Jefe de Guerra.', 'Я... Я об этом не подумал, Вождь.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (3669, 0, '대족장님! 명령만 내리십시오. 제 목숨은 대족장님의 인도가 없이는 의미가 없습니다.', 'Je ne vis que pour servir, Chef ! Ma vie est creuse et dénuée de sens sans votre lumière pour m\'éclairer.', 'Ich lebe nur, um Euch zu dienen, Kriegshäuptling. Mein Leben ist leer und bedeutungslos ohne Eure Führung.', '我活着就是为了为战争之王服务！没有你的指导，我的生活将空虚而无意义。', '我活著就是為了為大酋長服務!沒有你的指導，我的生活將空虛而無意義。', '¡Solo vivo para servir, Jefe de Guerra! Mi vida está vacía y carece de significado si no te tengo de guía.', '¡Solo vivo para servir, Jefe de Guerra! Mi vida está vacía y carece de significado si no te tengo de guía.', 'Я живу, чтобы служить, Вождь! Без ваших приказов моя жизнь пуста и бессмысленна.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
