INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (5735, 0, '날 그냥 \"왕\"이라고 부르도록... 그런데 나한테 줄 거라도 있느냐!', 'Appelez-moi “Patron”. Qu’avez-vous pour moi ?', 'Nennt mich \"Boss\". Was habt Ihr für mich?', '喊我“老大”。你们要供奉给我什么？！', '叫我「國王」。你為我帶來了什麼!', 'Llámame \"jefe\". ¿Qué tienes para mí?', 'Llámame \"jefe\". ¿Qué tienes para mí?', 'Зови меня \"босс\". Что там у тебя?!', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
