INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6185, 0, '그자는 도적에 거짓말쟁이일 뿐이므로 주군의 뜻대로 즉석에서 처형하겠습니다.', 'Cet homme est un voleur et un menteur. Je l’exécute sur-le-champ, comme le voudrait mon seigneur.', 'Ich tÃ¶te den Mann auf der Stelle, wie mein Lehnsherr es von mir erwartet, denn der Kerl ist ein Dieb und ein LÃ¼gner zugleich.', '正如我的君主所期望的那样，我当场就杀了那个人，因为他不过是一个小偷和骗子！', '我會當場殺了他，因為我的主人一定會要我這麼做，反正他不過是個小偷跟騙子。', 'Mato al hombre inmediatamente como mi señor habría esperado. No es más que un ladrón y un mentiroso.', 'Mato al hombre inmediatamente como mi señor habría esperado. No es más que un ladrón y un mentiroso.', 'Он будет убит на месте, как и хотел бы мой господин. Этот презренный тип – вор и лжец.', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6185, 1, '그자는 법을 어겼고 저는 법을 집행하겠다고 맹세했기 때문에 주군에게 끌고 가 처벌받게 하겠습니다.', 'Cet homme a violé la loi que j’ai juré de servir. Je le remets à mon seigneur pour qu’il soit châtié.', 'Ich bringe den Mann zu meinem Lehnsherrn fÃ¼r seine Bestrafung, da er das Gesetz gebrochen hat, und es meine beeidete Pflicht ist, das Gesetz zu wahren.', '我把那人交给了我的君主惩处，因为他违反了这儿的法律，我有责任惩处他。', '我會把他交給我的主人發落，因為他已經違反了這個地方的法律，而我的職責就是執行這個法律。', 'Llevo al hombre ante mi señor para que le castigue, porque ha roto las leyes que yo he jurado hacer respetar.', 'Llevo al hombre ante mi señor para que le castigue, porque ha roto las leyes que yo he jurado hacer respetar.', 'Я передаю преступника в руки властям. Мой долг следить за порядком, а он нарушил закон.', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6185, 2, '그자가 훔친 옥수수를 몰수하고 절도는 저주와 파멸에 이르는 길이라는 경고를 한 후 훈방 조치로 가족에게 돌려보내겠습니다.', 'Je confisque le maïs volé, je l’avertis que le vol est le plus court chemin vers la ruine et la mort, puis je le laisse retrouver sa famille.', 'Ich konfisziere das Getreide, das er stahl, warne ihn, das Stehlen der Weg zur Verdammnis und ZerstÃ¶rung ist, aber lasse ihn letztendlich zu seiner Familie zurÃ¼ckkehren.', '我没收了他偷来的玉米，警告他“偷窃是走向末日和毁灭的必经之路”，但是我让他回家了。', '我會把他偷走的玉米拿回來，警告他偷竊是通往末日跟毀滅的道路，但是我會放他回去跟家人團聚。', 'Confisco el maíz que ha robado, le digo que robar es el camino hacia la perdición y la destrucción y le dejo volver con su familia.', 'Confisco el maíz que ha robado, le digo que robar es el camino hacia la perdición y la destrucción y le dejo volver con su familia.', 'Я конфискую украденное зерно, предупрежу преступника о том, что воровство – путь к беде и разрушению, и отпущу его к семье.', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6185, 3, '그자의 가족이 며칠 동안 먹을 수 있을 만큼의 옥수수를 주면서 차라리 이 땅을 떠나라고 권고하겠습니다.', 'Je donne à l’homme assez de maïs pour nourrir sa famille pendant deux jours, et je lui conseille de quitter le pays.', 'Ich erlaube dem Mann genug Getreide fÃ¼r seine Familie mitzunehmen, damit er sie fÃ¼r ein paar Tage ernÃ¤hren kann und ermutige ihn, das Land zu verlassen.', '我允许他带了些足够他养几日家的玉米，鼓励了他几句，就让他离开了。', '我會讓他拿取足夠供他一家人糊口數日的玉米，並鼓勵他離開這個地方。', 'Permito que el hombre coja el suficiente maíz como para alimentar a su familia durante un par de días y le animo a que se vayan todos de ahí.', 'Permito que el hombre coja el suficiente maíz como para alimentar a su familia durante un par de días y le animo a que se vayan todos de ahí.', 'Я отдам ему столько зерна, чтобы он мог прокормить свою семью пару дней, и посоветую ему покинуть наши земли.', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6186, 0, '제 운명을 발견할 준비가 됐습니다!', 'Je suis prêt à découvrir mon avenir.', 'Ich bin bereit zu erfahren, was das Schicksal fÃ¼r mich bereithÃ¤lt!', '我已经准备好去寻找我的命运了！', '我已經準備好要知道我的命運了!', '¡Sí, quiero descubrir dónde yace mi fortuna!', '¡Sí, quiero descubrir dónde yace mi fortuna!', 'Я хочу узнать, что мне уготовано судьбой!', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6187, 0, '주군의 명령에 따라 그자를 처형하겠습니다. 그뿐만 아니라, 백성에게 못된 짓을 한 대가로 아주 고통스럽게 처형하겠습니다.', 'Je l\'exécute selon les ordres de mon seigneur et fais en sorte qu\'il endure de grandes souffrances avant de mourir en punition de ses crimes contre mon peuple.', 'Entsprechend der Order meines Herrn, werde ich ihn hinrichten. Er wird bis zum letzten Atemzug endlose Schmerzen erleiden. So werden ich ihm die Taten an meinem Volk heimzahlen.', '我按照君主的指示处死了他，并且让他在临死前饱受折磨地死去，作为他对我的人民所犯罪行的惩处。', '我會按照主人的命令處決他，而且用的是足以讓他在死前受盡折磨的方式，這樣才能償還他對我的同胞犯下的罪孽。', 'Lo ejecuto, eso es lo que me ha ordenado mi señor, y lo hago de tal forma que sufre mucho antes de morir castigado por los crímenes que cometió contra mi pueblo.', 'Lo ejecuto, eso es lo que me ha ordenado mi señor, y lo hago de tal forma que sufre mucho antes de morir castigado por los crímenes que cometió contra mi pueblo.', 'Я исполню веление моего господина. Его смерть будет долгой и мучительной, ибо он совершил преступление против моего народа.', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6187, 1, '주군의 명령에 따라 그자를 처형하겠지만 최대한 고통 없이 죽이겠습니다. 정의는 실현되어야 하지만 약간의 동정도 베풀 수 있다고 생각합니다.', 'Je l\'exécute selon les ordres de mon seigneur, mais de la façon la moins douloureuse possible. Justice doit être faite, mais je sais faire preuve de compassion.', 'Entsprechend der Order meines Herrn werde ich ihn hinrichten, doch werde ich es auf eine mÃ¶glich schmerzfreie Art tun. Zwar muss der Gerechtigkeit genÃ¼ge getan werden, doch soll dies mein Mitleid nicht mindern.', '我按照王上的命令处决他，但是我尽可能地让他少受痛苦。正义必须得到伸张，但我们也要心怀怜悯。', '我會按照主人的命令處決他，但是盡可能地讓他少受痛苦。正義必須得到伸張，但我可以心懷憐憫。', 'Lo ejecuto, eso es lo que me ha ordenado mi señor, pero lo hago de forma que sufra lo menos posible. Hay que hacer cumplir la justicia, pero también puedo mostrar compasión.', 'Lo ejecuto, eso es lo que me ha ordenado mi señor, pero lo hago de forma que sufra lo menos posible. Hay que hacer cumplir la justicia, pero también puedo mostrar compasión.', 'Я казню его, как приказал мой господин, но сделаю это по возможности быстро и безболезненно. Правосудие правосудием, но зверства тут ни к чему.', NULL, NULL, '', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (6187, 2, '그 친구가 자신의 무죄를 입증할 수 있도록 제 목숨을 걸고 그를 풀어 주겠습니다. 그리고 가능하면 그를 어떻게든 돕겠습니다.', 'Au péril de ma vie, je le libère pour lui permettre de prouver son innocence. Si cela m\'est possible, je fais tout pour l\'aider.', 'Ich riskiere mein Leben und befreihe ihn, damit er seine Unschuld beweisen kann. Soweit ich es kann, unterstÃ¼tze ich ihn auf jede erdenkliche Art und Weise.', '我愿意以自己生命的代价来拯救他，然后他就可以证明自己的清白。如果可以的话，我会不惜一切代价来帮助他。', '我會冒著我的生命危險釋放他，這樣他才可以證明他的清白。如果可以，我會用各種方式來幫助他。', 'Arriesgo mi propia vida y le libero para que pueda demostrar su inocencia. Si puedo, le ayudaré en lo que sea.', 'Arriesgo mi propia vida y le libero para que pueda demostrar su inocencia. Si puedo, le ayudaré en lo que sea.', 'Я рискну собственной жизнью и освобожу его, чтобы он мог доказать свою невиновность. Я помогу другу всем, чем смогу.', NULL, NULL, '', NULL, NULL, NULL, NULL, NULL);
