INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7675, 0, '나는 당신의 친구들인 오우거들을 수없이 죽였습니다, 란트레서. 두려움 따윈 모릅니다.', 'J\'ai tué un grand nombre de vos ogres, Lantresor. Je n\'ai pas peur.', 'Ich habe viele Eurer Oger getÃ¶tet, Lantresor. Ich habe keine Furcht.', '我已经杀了很多你说的食人魔，兰特瑞索。我并不害怕。', '我殺了你很多巨魔，蘭崔索。我什麼都不怕。', 'He matado a muchos de tus ogros, Lantresor. No tengo miedo.', 'He matado a muchos de tus ogros, Lantresor. No tengo miedo.', 'Я убил много твоих огров, Лантрезор. Я не боюсь.', NULL, NULL, '', '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7675, 1, NULL, NULL, 'Ich habe viele Eurer Oger getÃ¶tet, Lantresor. Ich habe keine Furcht.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7676, 0, '제가 할 일이 무엇입니까?', 'Que devrais-je faire ?', 'Was muss ich machen?', '我需要做什么？', '我要怎麼做?', '¿Qué debo hacer?', '¿Qué debo hacer?', 'Что от меня требуется?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7677, 0, '그렇다면 우리는 끝까지 당신과 싸울 것입니다, 란트레서. 당신이 우리 마을을 약탈하고 동족을 죽이는 걸 그냥 보고만 있지는 않을 거니까요.', 'Alors nous vous combattrons jusqu\'à la mort, Lantresor. Nous ne resterons pas les bras croisés pendant que vous pillez nos villes et tuez notre peuple.', 'Dann werden wir Euch bis zum Ende bekämpfen, Lantresor. Wir werden nicht tatenlos zusehen, wie ihr unsere Städte plündert und unsere Leute tötet.', '那我们只能抗争到底，兰特瑞索。我不会眼睁睁地看着你掠夺我们的城镇，杀害我们的人民。', '那麼，我們會與你對抗到底，蘭崔索。我們不會呆站一旁任你掠奪我們的城鎮殺害我們的人民。', 'Lucharemos contra ti hasta el final. No nos quedaremos de brazos cruzados mientras saqueas nuestras ciudades y matas a nuestra gente.', 'Lucharemos contra ti hasta el final. No nos quedaremos de brazos cruzados mientras saqueas nuestras ciudades y matas a nuestra gente.', 'Тогда, Лантрезор, мы будем бороться с тобой до конца. Мы не будем стоять и смотреть, как ты грабишь наши города и убиваешь наших людей.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7678, 0, '제 동족이 원하는 것은 당신이 돌주먹 오우거들을 물리고 우리 영역에 대한 공격을 중단하는 것입니다. 그러면 우리도 군대를 철수할 것입니다.', 'Mon peuple demande que vous éloigniez vos ogres rochepoings et que vous cessiez toutes vos attaques sur notre territoire. En échange, nous retirerons aussi nos forces.', 'Mein Volk verlangt, dass Ihr Eure Oger der Felsfäuste zurückzieht und alle Angriffe auf unsere Gebiete unterlasst. Im Gegenzug werden wir unsere Streitkräfte ebenfalls zurückziehen.', '我们希望你能将石拳兽人后撤，离开我的领地。相应地，我们也会将部队撤回来。', '我的人民要求你撤退你的石拳巨魔並停止所有對我們領土的攻擊行動。然後，我們才會撤退我們的軍隊。', 'Mi gente quiere que retires a tus ogros Puño de Roca y que cesen los ataques a sus tierras. A cambio, nosotros también retiraremos a nuestras fuerzas.', 'Mi gente quiere que retires a tus ogros Puño de Roca y que cesen los ataques a sus tierras. A cambio, nosotros también retiraremos a nuestras fuerzas.', 'Мой народ просит тебя отозвать клан Тяжелого Кулака и больше не нападать на наши земли. А мы тогда тоже уведем войска.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7679, 0, '사과 드립니다. 당신을 화나게 할 생각은 없었습니다. 저는 제 동족을 대표해서 여기 왔습니다.', 'Toutes mes excuses. Je ne voulais pas vous offenser. Je viens parler au nom de mon peuple.', 'Ich bitte um Verzeihung, ich wollte Euch nicht beleidigen. Ich bin hier im Auftrag meines Volkes.', '很抱歉，我无意冒犯。我为我的人民而来。', '很抱歉。我無意冒犯。我是代表我的人民來到這裡。', 'Lo siento. No quería ofender. Estoy aquí en representación de mi pueblo.', 'Lo siento. No quería ofender. Estoy aquí en representación de mi pueblo.', 'Примите мои извинения. Я не хотел вас обидеть. Я нахожусь здесь по велению моего народа.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7680, 0, '그렇군요. 당신 종족에 대한 얘기는 들었지만 반오크를 직접 만날 줄은 꿈에도 생각 못했습니다.', 'J\'ai entendu parler de ceux de votre sorte, mais je pensais pas voir un jour un demi-sang.', 'Ich habe von Eurer Art gehört, aber ich hätte nie geglaubt, den Tag zu erleben, an dem ich ein Halbblut treffe.', '我听说过你这一族，但从未想到能亲眼见到混血龙类。', '我聽過你的種族，但沒想過我會遇到一個混血兒。', 'He oído hablar de tu raza, pero nunca pensé que conocería un media sangre.', 'He oído hablar de tu raza, pero nunca pensé que conocería un media sangre.', 'Я слышал о таких, как ты, но не думал дожить до дня, когда встречу полукровку...', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
