INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7762, 0, '제가 도와드릴 일은 없습니까, 대모님?', 'Y a-t-il quoi que ce soit que je puisse faire pour vous, grande-mère ?', 'Gibt es etwas, das ich für Euch tun kann, Großmutter?', '不过我还没听说这么可憎的事情，当我在这世界还不是完全体的时候，可恶的德莱尼就抓住我了。', '有沒有什麼事是我可以幫忙的呢，主母?', '¿Puedo hacer algo por ti, Abuela?', '¿Puedo hacer algo por ti, Abuela?', 'Могу я сделать для вас что-нибудь, Великая Мать?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7763, 0, '그 의무라는 것이 뭡니까?', 'Quel est ce devoir ?', 'Was ist diese Aufgabe?', '职责是什么？', '這個義務是什麼?', '¿Cuál es tu función?', '¿Cuál es tu función?', 'Что это за долг?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7764, 0, '떠난다고요? 어떻게 그럴 수가 있습니까?', 'Partir ? Comment pouvez-vous décider de partir ?', 'Fortgehen? Was meint Ihr damit?', '离开？为什么会选择离开？', '離開?你怎麼可以選擇離開?', '¿Ido? ¿Cómo puedes decidir irte?', '¿Ido? ¿Cómo puedes decidir irte?', 'Ушли? Как можно решить уйти?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7765, 0, '어떻게 하면 좋을까요? 수많은 시도를 해봤습니다. 나그란드의 주민들을 돕기 위해 최선을 다했고요. 하지만, 가로쉬에게 다가가려 할 때마다 그는 저를 물리치더군요.', 'Que peut-on faire ? J’ai essayé de bien des manières. J’ai fait de mon mieux pour aider les gens de Nagrand. Chaque fois que j’ai approché Garrosh, il m’a rejeté.', 'Was kann man da tun? Ich habe viele verschiedene Dinge versucht. Ich habe mein bestes getan, um den Leuten von Nagrand zu helfen. Jedes Mal, wenn ich an Garrosh herantrete, schickt er mich fort.', '怎么办？我已经尝试过很多方法了。我尽全力帮助我在纳格兰的人民。每次我接近加尔鲁什，他都对我不屑一顾。', '還有什麼可以做?我試著去做許多不同的事，我盡全力去幫助納葛蘭的人民，每次我接近卡爾洛斯，他就打發我走。', '¿Qué se puede hacer? He intentado muchas cosas. He hecho todo lo posible por ayudar a las gentes de Nagrand. Cada vez que me he acercado a Garrosh, me ha echado.', '¿Qué se puede hacer? He intentado muchas cosas. He hecho todo lo posible por ayudar a las gentes de Nagrand. Cada vez que me he acercado a Garrosh, me ha echado.', 'Что можно поделать? Я что только не испробовал! Я трудился изо всех сил, помогая жителям Награнда. И всякий раз, как я обращался к Гаррошу, он мне отказывал.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7766, 0, '그 말을 믿으십니까?', 'Vous y croyez ?', 'Glaubt Ihr das?', '你信吗？', '你相信這個嗎?', '¿Eso crees?', '¿Eso crees?', 'Ты в это веришь?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7767, 0, '대모님, 이곳 오크들을 모두 키우신 겁니까?', 'Vous avez élevé tous les orcs qui sont ici, grande-mère ?', 'Ihr habt all die Orcs hier aufgezogen, Großmutter?', '你养育了这里所有的兽人，祖母？', '主母，妳養育了這裡全部的獸人?', '¿Tú criaste a todos estos orcos, Abuela?', '¿Tú criaste a todos estos orcos, Abuela?', 'Ты подняла здесь всех орков, Великая Мать?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7768, 0, '대모님, 안녕하십니까. 가로쉬에게 저를 보자고 하셨다는 얘기를 듣고 찾아뵈었습니다.', 'Je vous salue, grande-mère. Garrosh m’a dit que vous souhaitiez me parler.', 'Hallo, Großmutter. Garrosh sagte, Ihr wolltet mich sprechen.', '你好，祖母。加尔鲁什告诉我说你有话要跟我讲。', '主母，你好。 卡爾洛斯說你想和我談談。', 'Hola, Abuela. Garrosh me dijo que querías hablar conmigo.', 'Hola, Abuela. Garrosh me dijo que querías hablar conmigo.', 'Здравствуй, Великая Мать. Гаррош сказал, что ты хочешь со мной поговорить.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (7768, 1, '대모님, 이제는 가로쉬를 구제할 방법이 없습니다. 마그하르를 돕는 제 모습을 지켜본 가로쉬가 자신이 지도자로서 자질이 한참은 부족하다고 판단한 것 같습니다.', 'Garrosh ne peut plus être sauvé, grande-mère. J\'ai bien peur, en aidant les Mag\'har, d\'avoir convaincu Garrosh qu\'il n\'est pas digne d\'être chef.', 'Garrosh kann nicht erlöst werden, Großmutter. Ich fürchte, dass ich mit meiner Hilfe den Mag\'har gegenüber Garrosh davon überzeugt habe, dass er unfähig ist, Anführer zu sein.', '加尔鲁什无可救药了，祖母。我在帮助玛格汉的时候就担心这件事情了，我认为加尔鲁什不适合做一个领导者。', '卡爾洛斯是無可救藥的，主母。我懼怕在幫助瑪格哈的過程同時，我已經使卡爾洛斯認為他不適合成為領導人。', 'Garrosh no puede ser perdonado, Abuela. Me temo que al ayudar a los Mag\'har he convencido a Garrosh de que no puede ser el líder.', 'Garrosh no puede ser perdonado, Abuela. Me temo que al ayudar a los Mag\'har he convencido a Garrosh de que no puede ser el líder.', 'Гарроша уже не спасти, Великая Мать. Боюсь, что, моя помощь маг\'харам привела Гарроша к выводу, что он не годится в лидеры.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
