INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21290, 0, '내 질문에 대답해, 이 오우거야!', 'J\'ai besoin de réponses, l\'ogre !', 'Ich will Antworten, Oger!', '我需要答案，食人魔！', '我要答案，巨魔!', '¡Necesito respuestas, ogro!', '¡Necesito respuestas, ogro!', 'Мне нужны ответы, огр!', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21291, 1, '돌주먹 일족이 이렇게 멀리까지 온 이유가 뭐지? 알다시피 여기는 쿠레나이의 영역이지 않나?', 'Pourquoi les Rochepoings sont-ils si loin de chez eux ? Tu sais que c\'est un territoire Kurenaï.', 'Warum sind die Felsfäuste so weit hier draußen? Ihr wisst, dass dies hier Territorium der Kurenai ist.', '为什么巨石之拳离这这么远？你知道那是怪物的领土。', '為什麼石拳跑到那麼遠的地方去?你知道這是卡爾奈的領土。', '¿Por qué llegan los Puño de Roca hasta aquí? Sabes que este es territorio Kurenai.', '¿Por qué llegan los Puño de Roca hasta aquí? Sabes que este es territorio Kurenai.', 'Почему огры Тяжелого Кулака зашли так далеко? Вы же знаете, что это земли куренай!', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21292, 2, '그래서 원하는 건 뭐든지 먹을 수 있다고 생각했어? 네가 먹으려고 하는 건 텔라아르의 뒤틀린 드레나이들이라고.', 'Et tu penses que tu peux manger tout ce que tu veux ? Apparemment, tu essayes de manger les Roués de Telaar.', 'Und Ihr denkt, Ihr könnt alles essen was Ihr wollt? Ihr versucht offensichtlich, die Zerschlagenen von Telaar zu essen.', '你认为你想吃什么就吃什么吗？很显然你正在尝试吃破碎的东西。', '你以為你可以吃任何想吃的東西嗎?很明顯地你想吃泰拉的破碎者。', '¿Y crees que puedes comer todo lo que quieras? Obviamente intentas comerte a los Tábidos de Telaar.', '¿Y crees que puedes comer todo lo que quieras? Obviamente intentas comerte a los Tábidos de Telaar.', 'И ты думаешь, что можешь вот так взять и съесть все, что захочешь? Ты, очевидно, пытаешься съесть Сломленного из Телаара.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21293, 3, '그들에게 피해를 주는 건 전쟁을 의미해, 룸프! 전쟁 말이야!', 'Cela veut dire que c\'est la guerre, Gros-tas ! La guerre !', 'Das bedeutet Krieg, Brocken! Krieg, sage ich!', '北部，希尔盖？', '這代表戰爭，浪普!我說戰爭!', '¡Esto significa la guerra, Zoquete! ¡La guerra, te digo!', '¡Esto significa la guerra, Zoquete! ¡La guerra, te digo!', 'Это означает войну, Глыб! Войну!', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21295, 0, '나는 당신의 친구들인 오우거들을 수없이 죽였습니다, 란트레서. 두려움 따윈 모릅니다.', 'J\'ai tué un grand nombre de vos ogres, Lantresor. Je n\'ai pas peur.', 'Ich habe viele Eurer Oger getötet, Lantresor. Ich habe keine Furcht.', '我已经杀了很多你说的食人魔，兰特瑞索。我并不害怕。', '我殺了你很多巨魔，蘭崔索。我什麼都不怕。', 'He matado a muchos de tus ogros, Lantresor. No tengo miedo.', 'He matado a muchos de tus ogros, Lantresor. No tengo miedo.', 'Я убил много твоих огров, Лантрезор. Я не боюсь.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21310, 0, '잠시만 얘기를 나누고 싶습니다.', 'Pourriez-vous me consacrer un moment, monsieur ?', 'Habt Ihr einen Moment Zeit für mich, mein Herr?', '我想占用你一点时间，先生。', '請留步，先生。', 'Necesito un momento de su tiempo, señor.', 'Necesito un momento de su tiempo, señor.', 'Уделите мне минутку вашего времени, сэр.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21311, 0, '그럼요. 물론입니다. 바로 이 건물 안에 당신에게 보여드릴 것이 있습니다, 앤빌워드 씨.', 'Hum… oui, bien sûr. Mais il y a quelque chose que vous devriez voir dans ce bâtiment, M. Garde-enclume.', 'Warum... ja, natürlich. Ich möchte Euch etwas zeigen, hier, in diesem Gebäude dort, Meister Ambossel.', '为什么呢，当然。我有一些事情需要在阁楼中向您汇报，安维尔德先生！', '什麼...喔，當然。我要給你看的東西就在這棟大樓裡，安維爾伍德先生。', 'Vaya... sí, por supuesto. Tengo algo que mostrarle dentro de ese edificio, Prospector Guardayunque.', 'Vaya... sí, por supuesto. Tengo algo que mostrarle dentro de ese edificio, Prospector Guardayunque.', 'Да... конечно же, Страж Наковальни. Я должен показать вам кое-что в этом здании.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21312, 0, '안녕하십니까, 아이트리그. 검은바위 첨탑에서 소식을 가져왔습니다.', 'Bonjour, Eitrigg. J\'apporte des nouvelles du pic Rochenoire.', 'Hallo, Etrigg. Ich bringe Neuigkeiten von der Schwarzfelsspitze.', '你好，伊崔格。我从黑石塔带来了些消息。', '你好，伊崔格。我從黑石塔為你帶來消息。', 'Hola, Eitrigg. Traigo noticias de la Cumbre de Roca Negra.', 'Hola, Eitrigg. Traigo noticias de la Cumbre de Roca Negra.', 'Здравствуй, Эйтригг. У меня есть вести с Пика Черной горы.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21313, 0, '이런 말도 안 됩니다. 대족장님은 오직 한 분이십니다, 아이트리그!', 'Il n\'y a qu\'un chef de guerre, Eitrigg !', 'Es gibt nur einen Kriegshäuptling, Etrigg!', '只有一个大酋长，伊崔格！', '只有一個大酋長，伊崔格!', '¡Solo hay un Jefe de Guerra, Eitrigg!', '¡Solo hay un Jefe de Guerra, Eitrigg!', 'Есть только один Вождь, Эйтригг!', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21314, 0, '무슨 말씀이세요?', 'Qu\'est-ce que vous voulez dire ?', 'Was meint Ihr damit?', '你是什么意思？', '你這話是什麼意思?', '¿A qué te refieres?', '¿A qué te refieres?', 'Что вы имеете в виду?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
