INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21316, 0, '그러도록 하지요, 아이트리그.', 'Je vous rappellerai cette offre, Eitrigg.', 'Ich komme auf das Angebot zurück, Etrigg.', '我会接受这个任务，伊崔格。', '伊崔格，我會幫你的忙的。', 'Te tomo la palabra, Eitrigg.', 'Te tomo la palabra, Eitrigg.', 'Ловлю тебя на слове, Эйтригг.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21317, 0, '아. 그렇게 해서 검은무쇠 드워프를 검은바위 첨탑 아래로 밀어낸 것이군요.', 'Ah, c\'est donc ainsi qu\'ils ont chassé les Sombrefers vers les zones inférieures du pic.', 'Ah, so verdrängten sie die Dunkeleisenzwerge also in die unteren Teile der Spitze.', '啊，这就是他们怎么把黑铁感到塔底的。', '啊，他們就是這樣將黑鐵驅逐到黑石塔下部的。', 'Ah, así es como empujaron a los Hierro Negro a las partes inferiores de la Cumbre.', 'Ah, así es como empujaron a los Hierro Negro a las partes inferiores de la Cumbre.', 'Хм, значит, вот так они и загнали дворфов Черного Железа на нижние ярусы Пика.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21318, 0, '무슨 방법이 있지 않을까요?', 'Il existe peut-être un moyen ?', 'Gibt es vielleicht eine Möglichkeit?', '没准这里有条路？', '或許還有別的方法進入?', '¿Puede haber otro camino?', '¿Puede haber otro camino?', 'Может быть, есть какой-то путь?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21319, 0, '그렇게 하겠습니다, 아이트리그.', 'Comme vous voulez, Eitrigg.', 'Wie Ihr wünscht, Etrigg.', '随你的便吧，伊崔格。', '如你所願，伊崔格。', 'Como desees, Eitrigg.', 'Como desees, Eitrigg.', 'Как пожелаешь, Эйтригг.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21321, 0, '부적이란 건 뭡니까?', 'Qu\'est-ce que Juju ?', 'Was ist Juju?', '魂能是什么?', '魂能是什麼?', '¿Qué es Juju?', '¿Qué es Juju?', 'Что такое джуджу?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21322, 0, '죄송합니다. 제 말을 알아들으실 줄 몰랐습니다. 이곳에서 뭘 하고 계신 겁니까?', 'Toutes mes excuses, je ne savais pas que vous compreniez ce que je dis. Que faites-vous ici ?', 'Vergebt mir - ich wusste nicht, dass Ihr versteht, was ich sage. Was genau macht Ihr hier draußen?', '抱歉，我没想到你能听懂我说的话。你在这里做什么？', '很抱歉，我沒有意識到你可以理解我說的話。你在這兒做什麼?', 'Mis disculpas, no advertí que podías entender lo que estaba diciendo. ¿Qué estás haciendo aquí fuera?', 'Mis disculpas, no advertí que podías entender lo que estaba diciendo. ¿Qué estás haciendo aquí fuera?', 'Прошу прощения, я не знал, что вы понимаете мои слова. А что вы, собственно, здесь делаете?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21323, 0, '생각할 게 많으십니까? 걱정거리가 있으시면 말씀해 보시지요.', 'Vraiment ? Peut-être devriez-vous me dire ce qui vous tourmente.', 'Ach ja? Vielleicht solltet Ihr mir sagen, was Euch bedrückt.', '是吗？也许你该告诉我是什么让你烦恼的。', '是嗎?或許你該告訴我是什麼讓你煩惱。', '¿Sí? Quizás deberías decirme qué te preocupa.', '¿Sí? Quizás deberías decirme qué te preocupa.', 'Правда? Возможно, тебе стоит рассказать мне о своей проблеме.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21324, 0, '어떤 계약이었죠?', 'Quel marché ?', 'Was für eine Vereinbarung?', '什么约定？', '什麼交易?', '¿Qué trato?', '¿Qué trato?', 'В чем дело?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21325, 0, '고랄루크가 어떻게 계약을 깨뜨렸습니까?', 'Comment a-t-il rompu le marché ?', 'Wie hat er denn die Vereinbarung gebrochen?', '他是怎么破坏约定的？', '他是怎麼破壞協議的?', 'Entonces, ¿cómo rompió el trato?', 'Entonces, ¿cómo rompió el trato?', 'А как именно он нарушил сделку?', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21326, 0, '제가 도와드릴 수 있을지도 모르겠군요. 사티로스 로락스, 당신과 거래를 하겠습니다. 고랄루크 앤빌크랙이라고 하는 녀석을 처치하고 버려지지 않은 가슴보호갑을 찾아오면 그 대가로 악마의 룬 가슴보호갑 만드는 법을 가르쳐주십시오.', 'Je peux peut-être vous aider. Je vais faire un marché avec vous, satyre. Je vais retrouver ce plastron et tuer cette bête de Goraluk Brisenclume. En échange, apprenez-moi à créer le plastron.', 'Vielleicht kann ich Euch ja behilflich sein. Ich schlage Euch einen Handel vor, Satyr: Ich beschaffe Euch diese ungeschmiedete Brustplatte und töte die Bestie, Goraluk Hammerbruch. Und im Gegenzug lehrt Ihr mich, wie man die Brustplatte herstellt.', '也许我能提供一些帮助。我们来做个交易吧，萨特。我会杀了那头巨兽并带回未铸造的胸甲。作为交换，你要教我如何制造这个胸甲。', '或許我能幫上忙。薩特，我們來談筆交易吧。我去取回這塊未鑄造的胸甲並殺死野獸古拉魯克。作為交換，你要教我如何製造胸甲。', 'Quizás pueda ser de ayuda. Haré un trato contigo, sátiro. Recuperaré esa coraza sin forjar y derrotaré a la bestia, Goraluk Yunquegrieta. A cambio de esta tarea me enseñarás a crear el peto.', 'Quizás pueda ser de ayuda. Haré un trato contigo, sátiro. Recuperaré esa coraza sin forjar y derrotaré a la bestia, Goraluk Yunquegrieta. A cambio de esta tarea me enseñarás a crear el peto.', 'Возможно, я смогу тебе помочь. Я заключу с тобой сделку, сатир. Я добуду этот распаянный нагрудник и убью Горалука Треснувшую Наковальню. А ты покажешь мне, как делать нагрудники.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
