INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (21331, 0, '미쳐 버렸군요, 네파리우스. 수수께끼 같은 말만 내뱉다니...', 'Vous avez perdu la tête, Nefarius. Vous parlez en énigmes.', 'Ihr habt Euren Verstand verloren, Nefarius. Ihr sprecht in Rätseln.', '你像是迷失了自己，奈法利安。我听不懂你在说什么。', '你已經失去理智，奈法利斯。你在說什麼啞謎。', 'Has perdido la cabeza, Nefarius. Hablas con acertijos.', 'Has perdido la cabeza, Nefarius. Hablas con acertijos.', 'Ты совсем свихнулся, Нефарий. Ты разговариваешь загадками.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
