INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (56001, 0, '아니오, 울라텍. 그분은 당신의 비밀을 알고 계시오. 당신의 소로스의 헬누라스 군주와 음모를 꾸미고 있다는 사실을 아신단 말이지.', 'Non, Ulathek. Il connaît votre secret. Il sait que vous complotez avec le seigneur Hel’nurath de Xoroth.', 'Nein, Ulathek. Er kennt Euer Geheimnis. Er weiß, Ihr verschwört Euch mit Lord Hel\'nurath von Xoroth.', '不，乌拉泰克。他知道你的秘密。他知道你跟克索诺斯的赫尔努拉斯之间的阴谋。', '不，烏拉泰克。他知道你的秘密。他知道你跟克索諾斯，赫爾努拉斯領主的陰謀。', 'No, Ulathek. Conoce tu secreto. Se ha enterado de tu plan con Lord Hel\'nurath de Xoroth.', 'No, Ulathek. Conoce tu secreto. Se ha enterado de tu plan con Lord Hel\'nurath de Xoroth.', 'Нет, Улатек. Он знает твою тайну. Он знает, что ты в сговоре с лордом Хел\'нуратом из Зорота.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (56002, 0, '거짓말이 아니다, 배신자. 베인할로우 님께서 너의 심장을 원하신다. 내가 너의 심장을 가져갈 것이다.', 'Ce n’est pas un mensonge, traître. Creuseplaie veut votre cœur, et je vais le lui apporter.', 'Dies ist keine Lüge, Verräter. Schattenfluch will Euer Herz und ich werde es ihm besorgen.', '叛徒，我可没骗你。魔王贝恩霍勒想要你的命。', '叛徒，我可沒騙你。魔王貝恩霍勒想要你的心臟，我現在就取給他。', 'No es una mentira, traidor. Pesadilla quiere tu corazón y yo voy a llevárselo.', 'No es una mentira, traidor. Pesadilla quiere tu corazón y yo voy a llevárselo.', 'Это не ложь, предатель. Лорд Гиблопуст хочет получить твое сердце, и я его для него достану.', NULL, NULL, NULL, '', NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57000, 0, '당신의 역할은 무엇입니까?', 'Quelle est votre fonction ?', 'Welche Funktion habt Ihr?', '你的职责是什么？', '你的職責是什麼?', '¿Cuál es tu función?', '¿Cuál es tu función?', 'Какие обязанности вы исполняете?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
