INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57003, 0, '네? \"방문 일정 변경\"이라고요? 그게 무슨 뜻입니까?!', 'Excusez-moi ? Nous avons été \"reprogrammés pour inspection\" ? Qu\'est-ce que cela signifie ?!', 'Wie bitte? Es ist ein \"erneuter Besuch\" geplant? Was soll denn das heißen?', '什么？我们的世界“被安排了新的探访计划”？这是什么意思？！', '什麼?我們的世界「被安排了新的探訪計畫」?這是什麼意思?!', '¿Dime? Nos han reprogramado para la aparición\"? ¡¿Eso qué quiere decir?!\"', '¿Dime? Nos han reprogramado para la aparición\"? ¡¿Eso qué quiere decir?!\"', 'Прошу прощенья? \"Время визита было изменено\"? Что это вообще значит?!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57004, 0, '그렇다면... 울둠 안에는 무엇이 있습니까?', 'Alors... qu\'y a-t-il à l\'intérieur d\'Uldum?', 'Was befindet sich denn in Uldum?', '那么……奥丹姆里面到底有些什么？', '那麼...奧丹姆裡面到底有些什麼?', 'Y bien... ¿qué hay en el interior de Uldum?', 'Y bien... ¿qué hay en el interior de Uldum?', 'Ну... и что же находится в Ульдуме?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
