INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57006, 3, '보들리, 무슨 일인지 말해 주십시오.', 'Bodley, expliquez-moi ce qui se passe.', 'Bodley, was geht hier vor?', '伯德雷，告诉我发生了什么事。', '布德利，告訴我發生了什麼事。', 'Bodley, cuéntame qué está pasando.', 'Bodley, cuéntame qué está pasando.', 'Бодли, объясни мне, что происходит.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
