INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57008, 0, '아뇨, 그럴 리가 있습니까? 계속 얘기해 주세요! 그러니까... 음, 여기 공기가... 좀 탁해서요.', 'Non, non, continuez, je vous en prie… C’est juste que l’air est un peu, heu, enfumé.', 'Nein, nein, fahrt bitte fort... Das liegt nur an der rauchigen Luft hier.', '不，不，请继续……这里的空气……似乎，呃，有点浑浊……', '不，不，請繼續...我只是覺得這裡的空氣很，呃，煙霧彌漫。', 'Sí, sí, por favor continúa... es que el aire aquí está... cargado.', 'Sí, sí, por favor continúa... es que el aire aquí está... cargado.', 'Нет-нет, продолжайте... здесь просто воздух такой... э-э-э... дымный.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57009, 0, '그래서요, 보들리?', 'Continuez, Bodley.', 'Bitte erzählt weiter, Bodley.', '请继续，伯德雷。', '請繼續，布德利。', 'Por favor Bodley, continúa.', 'Por favor Bodley, continúa.', 'Прошу вас, Бодли, продолжайте.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57010, 0, '그래도 거의 성공한 것 아니었습니까?', 'Je pensais que vous aviez presque terminé ?', 'Ich dachte, Ihr wärt fast fertig?', '完了？', '我以為你快完成了?', 'Creí que ya estabas acabando.', 'Creí que ya estabas acabando.', 'Я смотрю, ты почти закончил?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
