INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (57023, 1, '미끼에 쓸 진흙노린재는 어떻게 잡죠?', 'Comment attrape-t-on les puants de vase pour votre appât ?', 'Wie fange ich die Matschstinker für deinen Köder?', '我要怎么样才能捕捉到鱼饵所需的臭泥鱼？', '我要怎麼樣才能捕捉到魚餌所需的臭泥魚?', '¿Cómo puedo coger a los Fangoapestosos para el cebo?', '¿Cómo puedo coger a los Fangoapestosos para el cebo?', 'Как мне поймать грязнотинников для вашей наживки?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59563, 0, '공무 때문에 왔습니다, 존. 치안대장 윈저에 대한 정보가 필요한데, 마지막으로 그를 본 게 언제인지 말씀해주십시오.', 'Affaires officielles, John. J\'ai besoin d\'informations sur le maréchal Windsor. Dites-moi quand vous l\'avez vu pour la dernière fois.', 'Offizielle Angelegenheit, John. Ich brauche Informationen über Marschall Windsor. Erzählt mir vom letzten Mal, als Ihr ihn gesehen habt.', '约翰，我有公务在身。我需要一切有关温德索尔元帅的信息。你最后一次见他是什么时候？', '約翰，我有公務在身。我需要一切有關溫德索爾元帥的資訊。你最後一次見他是什麼時候?', 'Asuntos oficiales, John. Necesito cierta información sobre el mariscal Windsor. Háblame de la última vez que lo viste.', 'Asuntos oficiales, John. Necesito cierta información sobre el mariscal Windsor. Háblame de la última vez que lo viste.', 'Это официальное дело, Джон. Мне нужны сведения о маршале Виндзоре. Расскажи мне о последней вашей встрече.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59563, 1, '독을 짜내세요, 존.', 'Trais-moi, John.', 'Melkt mich, John.', '挤“奶”吧，约翰。', '約翰，餵我奶喝。', 'Ordéñame, John.', 'Ordéñame, John.', 'Подои меня, Джон.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59564, 0, '그래서 어떻게 됐나요?', 'Qu\'avez-vous fait alors ?', 'Und was habt Ihr gemacht?', '你干了些什么？', '你幹了些什麼?', '¿Entonces qué hicisteis?', '¿Entonces qué hicisteis?', 'И что же вы сделали?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59565, 0, '알아들을 수 있게 설명해 주세요. 삼십육계 줄행랑이나 당신 아버지에게는 관심 없습니다.', 'Tout ça ne veut rien dire, nain. Je ne m\'intéresse pas à vos biscuits ni à votre grand-père.', 'Redet kein dummes Zeug, Zwerg. Ich will nichts mit Eurem Kräcker, Eurem Papa oder irgendeiner Form von \'Diskreditieren\' zu tun haben.', '矮人，脑子放清醒点。我可没问你什么老爹啊名誉啊之类的事儿。', '矮人，腦子放清醒點。我可沒問你什麼老爹啊名譽啊之類的事兒。', 'Empieza a tener sentido, enano. No quiero tener nada que ver con tu pastel, tu papá o con cualquier clase de incredulidad.', 'Empieza a tener sentido, enano. No quiero tener nada que ver con tu pastel, tu papá o con cualquier clase de incredulidad.', 'Хватит пороть чушь, дворф. Меня не интересуют ни твои байки, ни твои сплетни, ни весь прочий \"компромат\".', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59566, 0, '무쇠폭군이라고요?', 'Hostefer ?', 'Eisenfeind?', '反对者？', '反對者?', '¿Ferromazo?', '¿Ferromazo?', 'Железогубитель?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
