INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59568, 0, '그렇게 해서 윈저가 죽은 것이로군요...', 'Alors c\'est ainsi que Windsor est mort...', 'So ist Windsor also gestorben...', '温德索尔就那样死了……', '溫德索爾就那樣死了...', 'Entonces así es como murió Windsor...', 'Entonces así es como murió Windsor...', 'Вот так и умер Виндзор...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59569, 0, '그렇다면 어떻게 해서 죽은 겁니까?', 'Alors comment est-il mort ?', 'Und wie ist er gestorben?', '他是怎么死的？', '他是怎麼死的?', '¿Entonces cómo murió?', '¿Entonces cómo murió?', 'А как он умер?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59570, 0, '좋아요. 그렇다면 대체 윈저는 어디에 있는 겁니까? 가만! 지금 당신 술 취했어요?', 'OK, mais il est où alors ? Attendez une minute ! Vous êtes ivre ?', 'Gut, also wo, zum Teufel, ist er? Moment mal! Seid Ihr betrunken?', '好吧，他到底在哪儿？等一会！你喝醉了吗？', '好吧，他到底在哪兒?等一會!你喝醉了嗎?', 'Vale, ¿entonces dónde diablos está? ¡Espera un momento! ¿Has bebido?', 'Vale, ¿entonces dónde diablos está? ¡Espera un momento! ¿Has bebido?', 'Ну и где его демоны носят? Постой-ка! Ты что, $gпьян:пьяна;?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
