INSERT INTO `locales_gossip_menu_option` (`menu_id`, `id`, `option_text_loc1`, `option_text_loc2`, `option_text_loc3`, `option_text_loc4`, `option_text_loc5`, `option_text_loc6`, `option_text_loc7`, `option_text_loc8`, `box_text_loc1`, `box_text_loc2`, `box_text_loc3`, `box_text_loc4`, `box_text_loc5`, `box_text_loc6`, `box_text_loc7`, `box_text_loc8`) VALUES (59572, 0, '300명이라고요? 검은무쇠 녀석들이 윈저를 죽이고 검은바위 나락으로 끌고 간 겁니까?', 'Trois cents ? Alors les Sombrefers l\'ont tué avant de le transporter dans les Profondeurs ?', '300? Also haben die Dunkeleisenzwerge ihn getötet und in die Tiefen geschleppt?', '300？那么说黑铁矮人杀死了他，并把他拖到了深渊里？', '300?那麼說黑鐵矮人殺死了他，並把他拖到了深淵裡?', '¿300? ¿Así que los Hierro Negro lo mataron y lo arrojaron a las Profundidades?', '¿300? ¿Así que los Hierro Negro lo mataron y lo arrojaron a las Profundidades?', '300? То есть дворфы Черного Железа убили его и утащили в глубины?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
